    for (Class<? extends Panel> clazz : AHardware.getHardwareAccessPanelClasses())
      System.out.println("- "+clazz.getName());
    System.out.println("EOL\n");

    System.out.println("Diposing of hardware ...");
    CslHardware.getInstance().dispose();

